pragma solidity ^0.4.0;

contract invoice {

  address public originator;
  address public payer;
  uint amount_owed;
  bytes32 public invoice_name;



  constructor(
	      bytes32 _invoice_name,
	      address _payer,
	      uint _amount_owed
	      )public {
    originator = msg.sender;
    payer = _payer;
    invoice_name = _invoice_name;
    amount_owed = _amount_owed;
  }

  function get_balance() public view returns (uint){

    //only those involved with the debt need know the amount
    require(
	    msg.sender == payer ||
	    msg.sender == originator,
	    'Not all knowledge should be known');

    return amount_owed;
  }

    function pay() public payable {

    //payment must be from deptor
    require(
	    msg.sender == payer,
	    'This weight is not yours to bear');


    amount_owed -= msg.value;



    }
}
